import {  useNavigate, useParams } from 'react-router-dom';
import { useApi } from '../shared/API';
import { Job } from '../types/Job';
import { UpdateJob } from './UpdateJob';


export const JobDetails = () => {

    // Constants and Hooks
    const { jobID } = useParams<{jobID: string}>();
    let [job, setJob] = useApi<Job[]>(`?action=job&jobID=${jobID}`);
    const navigate = useNavigate();

    console.log('jobID', jobID);
    console.log('job: ', job);

    if(!job){return (<p>Lade...</p>)}

    //Event hamdling
    const onUpdate = ()=> {
    navigate(`/UpdateJob/${jobID}`);
    }

    return (
        <>
        <h3>Job Details</h3>
<div className="container">
  <div className="row">
    <div className="col">
      Job Title
    </div>
    <div className="col-5">
      {job[0].jobTitle}
    </div>
    <div className="col">
      Date
    </div>
    <div className="col">
    {job[0].jobDate.slice(0,10)}
    </div>
  </div>
  <div className="row">
    <div className="col">
      Description
    </div>
    <div className="col-5">
    {job[0].jobDescription}
    </div>
    <div className="col">
      Company
    </div>
    <div className="col">
    {job[0].compName}
    </div>
  </div>
  <div className="row">
    <div className="col">
      Details
    </div>
    <div className="col-5">
    {job[0].jobDetails}
    </div>
    <div className="col">
      Status
    </div>
    <div className="col">
    {job[0].jobStatus}
    </div>
  </div>
  <br />
  <div className="row">
    <div className="col">
    <button type="button" onClick={onUpdate} className="btn btn-primary btn-sm">Update</button>
    <button type="button" className="btn btn-warning btn-sm">Delete</button>

    </div>
    <div className="col">
    </div>
</div>
  </div>

</>
    )
}
